package _1_syntax_of_java;public class MyFirstClass {    public static void main(String[] args) {        System.out.println("hello Urmat");        System.out.print("Ur");        System.out.print("mat");        System.out.println("!");        System.out.printf("Число ПИ равно - %f!", 3.14159);    }}// {}->body// в одном классе нельзя ипользовать дважды один метод//CamelCase -//SnakeCase - My_First_Class//KebabCase - My-First-Class//Case-sensitive --> Hello/hello//Class/file names --> Upper case//method/variable names --> lower case//Class and file names --> class: Book/ File: Book.java//public static void main(String[] args) {}--> main which run the class//identification --> names of class, methods, variable//syntax rules --> a-z, A-Z. $ and _// print --> same line// println --> new line// out --> white// eer --> red// keywords --> we can not use  in identification (names)// comments --> //, /*, command + shift + /